const api="https://disease.sh/v3/covid-19/historical/all?lastdays=557",getData=async()=>{const response=await fetch(`${api}`);return response.ok?await response.json():Promise.reject(response.status)},result=getData();result.then(data=>{let date=Object.keys(data.cases),total=Object.values(data.cases),deaths=Object.values(data.deaths),recovered=Object.values(data.recovered);var ctx=document.getElementById("chart").getContext("2d");let myChart=new Chart(ctx,{type:"line",data:{labels:date,datasets:[{label:"Total Cases",data:total,borderColor:"rgba(255, 99, 132)",fill:!1},{label:"Recovered Cases",data:recovered,borderColor:"rgba(153, 102, 255, 1)",fill:!1},{label:"Deaths",data:deaths,borderColor:"rgba(75, 192, 192, 1)",fill:!1}]},options:{scales:{yAxes:[{scaleLabel:{display:!0,labelString:"Numbers in Thousands"}}],xAxes:[{scaleLabel:{display:!0,labelString:"Date(DD/MM/YYYY)"}}]},title:{display:!0,text:"Coronovirus Cases in the World for the last year to current"}}})}).catch(error=>{console.log("Error: ",error)});